INSERT INTO `wp_posts` VALUES (419, 1, '2013-10-06 14:56:43', '0000-00-00 00:00:00', 'The 11th Philippine Youth Congress on Information Technology (Y4iT) comes to a bigger venue on September 17 to 19 at the SMX Convention Center, Pasay City!

